export function initStickyNav(){console.log("Sticky Nav JavaScript chargé");const n=document.getElementById("stickyNav");if(!n){console.log("Sticky nav NOT found");return}console.log("Sticky nav trouvé");const s=["ingredients","steps","faq"];function t(){let e="ingredients";for(const t of s){const n=document.getElementById(t);n&&n.getBoundingClientRect().top<=120&&(e=t)}document.querySelectorAll("#stickyNav .navLink").forEach(t=>{const n=t.dataset.target===e;n?(t.classList.add("text-red-500"),t.classList.remove("text-gray-600")):(t.classList.add("text-gray-600"),t.classList.remove("text-red-500"))})}document.addEventListener("scroll",()=>requestAnimationFrame(t)),t();const e=document.getElementById("navPinBtn"),i=document.getElementById("navPinIcon");if(!e){console.log("Pin button NOT found");return}console.log("Pin button trouvé");function o(){console.log("Opening Pinterest to pin to user account");const e=encodeURIComponent(window.location.href),t=encodeURIComponent(document.title),n=document.querySelector('meta[property="og:image"]')?.content||"",s=encodeURIComponent(n),o=`https://pinterest.com/pin/create/button/?url=${e}&description=${t}&media=${s}`;window.open(o,"_blank","width=600,height=400")}e.addEventListener("click",function(e){e.preventDefault(),console.log("Pin button clicked - opening Pinterest"),o()}),e.classList.add("text-red-500")}